import React from "react";
import {
  Redirect, 
  Route
} from "react-router-dom";

function withAuthRedirect({ Component, ...props }) {
  debugger;
  return (
    <Route
      {...props}
      
      render={() =>
        props.isAuth 
        ? (
          <Component/>
        ) 
        : (
        <Redirect to={{pathname: "/teacher"}}/>)

      }
    />
  )
}
export default withAuthRedirect
