import React, {useContext, useEffect} from "react";
import {AuthContext} from "../auth/authContext";
import {Redirect} from "react-router";

export const WithAuthRedirect = (Component) => (props)=> {
  debugger
  const {user}=useContext(AuthContext)

  return (
      <div>
        {!user || user===""
            ? <Redirect to='/'/>
            : <Component/>
        }
      </div>
  )
 
}
