import React from "react";
import {
  Redirect, 
  Route
} from "react-router-dom";

function withAuthRedirect({ Component, ...props }) {
  return (
    <Route
      {...rest}
      render={() =>
        isAuth ? (
          <Component/>
        ) : (
          <Redirect
            to={{
              pathname: "/admin"
            }}
          />
        )
      }
    />
  )
}
export default withAuthRedirect
