import React from "react";

export const initialState = {
    isAuthenticated: false,
    user: null,
    status: null,
    student: null
};


export const authReducer=(state, action)=>{

    switch (action.type){
        case "LOGIN":

            return{
                ...state,
                isAuthenticated: true,
                user: action.payload
            }
        case "STUDENT_LOGIN":
            debugger
            return {
                ...state,
            }
    }
}


export default authReducer
