import React, {useEffect, useState} from 'react';
import "../Admin.css";


const ShowStudents = () => {

    const [students, setStudents]=useState([]);
    

    useEffect(() => {
        fetchData();
    },[students])

    const fetchData = async () => {
        let response = await fetch("http://localhost:3000/students/all")
        let responseJson = await response.json();

        setStudents(responseJson)
        console.log(students)
    }

    return (
        <div className="wrapper">
            <table >
                <tbody>
                  <tr>
                    <th>Student ID</th>
                    <th>Name</th>
                    <th>Surname</th>
                    <th>Mark</th>
                    <th>Presence</th>
                    <th>Time</th>
                </tr>
                </tbody>
                
                <tbody>
                    {students 
                   && students.map((val,index) => {
                        return (
                            <tr>
                                <td>{val.student_id}</td>
                                <td>{val.name}</td>
                                <td>{val.surname}</td>
                                <td>{val.presence}</td>
                                <td>{val.time}</td>
                            </tr>)
                    })
                    }
                </tbody>
                
            </table>
        </div>

    )
}
export default ShowStudents
