import React from "react";

export const initialState = {
    isAuthenticated: false,
    user: null,
    status: null,
    student: {
        authorizedId: null,
        name: null

    }
};


export const authReducer=(state, action)=>{

    switch (action.type){
        case "LOGIN":

            return{
                ...state,
                isAuthenticated: true,
                user: action.payload
            }
        case "STUDENT_LOGIN":
            debugger
            return {
                ...state,
                student: {...state.student, ...action.payload}

            }
    }
}


export default authReducer
