import React, {useEffect, useState} from 'react';
import './App.css';
import {BrowserRouter, Route } from "react-router-dom";
import Teacher from './components/Teacher/Teacher'
import Student from './components/Student/Student';
import HomePage from './HomePage/HomePage';
import Admin from './components/Admin/Admin';

import 'bootstrap/dist/css/bootstrap.min.css';


function App() {

    const [isLoggedIn, setIsLoggedIn] = useState(true)
    const [user, setUser] = useState(null | "Farid Aliyevs")

    const myCallback = (dataFromChild, user) => {
        if (dataFromChild) {
            setIsLoggedIn(true)
            setUser(user)
        }
    }

  return (
    <BrowserRouter>
      <Route exact path="/" render={() => <HomePage/>}/>
      <Route  path="/teacher" render={() => <Teacher parentData={myCallback}/>}/>
      <Route  path="/student" render={() => <Student/>}/>

       {isLoggedIn&& <Route  path="/admin" render={() => <Admin user={user}/>} />}
    </BrowserRouter>
  );
}

export default App;
