import React, {useContext, useEffect} from "react";
import {AuthContext} from "../auth/authContext";
import {Redirect} from "react-router";

export const withAuthRedirect = (Component) => {
  return function () {
    const {user} = useContext(AuthContext)

    return (
        <div>
          {!user || user === ""
              ? <Redirect to='/'/>
              : <Component user={user}/>
          }
        </div>
    )

  }
}
