import React,{useEffect, useState} from 'react';
import {Form,Button, Col, } from 'react-bootstrap'

const AddSession=()=>{
    const [courses, setCourses]=useState("")

    useEffect(()=>{
        getCourses();
    },[])

    // fetch to get courses
    const getCourses=async ()=>{
        let response= await fetch("http://localhost:3000/api/courses/all")
        let responseJson=await response.json()
        setCourses(responseJson);
    }

        //make fetch to extract students

        //make calculate session work

        //send created call to db


    return(
        <>
        <Form row sm={4}>
            <h3>Add an exam session for Course
                <select>
                    {courses
                        ?
                        courses.map((val,index)=>{
                           return (
                               <option key={index} name={val.crsName}>{val.crsName}</option>
                           )
                        })
                        : <option>Loading</option>
                    }
                </select>
            </h3>
            <Form.Row >
                <Form.Group as={Col} controlId="frmstudentnum">
                    <Form.Label style={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                    }}>Number of students for this course is  </Form.Label>
                </Form.Group>
            </Form.Row>
            <Form.Group>
                <Form.Label>List of available students</Form.Label>
                <Form.Control as="select" defaultValue="Choose...">
                    <option></option>
                </Form.Control>
            </Form.Group>

            <Form.Group controlId="sessiondate">
                <Form.Label>Start date of the exam session</Form.Label>
                <Form.Control type="date" />
            </Form.Group>

            <Form.Group controlId="sessiontime">
                <Form.Label>Start time of the exam session</Form.Label>
                <Form.Control type="time" />
            </Form.Group>

            <Form.Row>
                <Form.Group  controlId="sessionduration">
                    <Form.Label>Duration of the session(minutes)</Form.Label>
                    <Form.Control type="number" min="1" max="60" placeholder="10" />
                </Form.Group>

                <Form.Group controlId="sessionduration">
                    <Form.Label>Number of students for this exam session</Form.Label>
                    <Form.Control type="number" id="studnumforsession" min="1"  placeholder="10" />
                </Form.Group>

                <Form.Group  controlId="formGridZip">
                    <Form.Label>Number of needed sessions</Form.Label>
                    <Form.Control type="number" disabled />
                </Form.Group>
            </Form.Row>

            <Form.Group id="formGridCheckbox">
                <Form.Check type="checkbox" label="I have checked all credentials and accepting the creation of the exam" />
            </Form.Group>

            <Button variant="primary" type="submit" style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
            }}>
                Add the new session
     </Button>
        </Form>
        </>

    )
}


export default AddSession
