import React, {useEffect, useState} from 'react';
import {Form, Button, Col,} from 'react-bootstrap';


const ExecuteExam = () => {

    const [markInProgress, setMarkInProgress ]= useState({id:null, mark:null})
    const [students, setStudents] = useState([]);
    const [studentMark, setStudentMark] = useState(null);
    const [currentUser, setCurrentUser]=useState(null);
    // const markForCurrentStudent=useCurrentUserData(currentUser);


    useEffect(() => {
        fetchData();

    }, [])

    const fetchData = async () => {
        let response = await fetch("http://localhost:3000/api/students/available")
        let responseJson = await response.json();
        setStudents(responseJson)

    }

    const handleCurrentUser = (value) => {
        setMarkInProgress(prevState => ({
            
        }))
        // setStudentMark(Number(value));
    }

    // function useCurrentUserData(id){
    //     const [mark, setMark]=useState(studentMark);
    //     useEffect(()=>{
    //
    //     })
    //
    // }


    return (
        <>
            <div>
                <div className="execute-title">
                    <p>Exam created by TEACHER FOR COURSE.NAME OF JULY CALL</p>
                </div>
                <table>
                    <tbody>
                    <tr>
                        <th>â„–</th>
                        <th>Student ID</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Mark</th>
                        <th>Presence</th>
                        <th>Time</th>
                        <th>Grade</th>
                    </tr>
                    </tbody>

                    <tbody>
                    {students
                    && students.map((val, index, arr) => {

                        return (
                            <tr key={val.id}>
                                {Object.keys(val).map((key, index) => {

                                    return (
                                        <>
                                            <td key={key}>{val[key]}</td>

                                        </>
                                    )

                                })}

                                <input type="number"
                                       name={val.id}
                                       onFocus={()=>handleCurrentUser(val.id)}
                                       onChange={e=>setStudentMark(Number(e.target.value))}

                                />

                                <input type="submit"
                                       // onClick={()=>handle}
                                />

                            </tr>)
                    })
                    }
                    </tbody>

                </table>
            </div>
        </>

    )
}


export default ExecuteExam
