export const initialState = {
    isAuthenticated: false,
    user: null
};

const authReducer=(state, action)=>{
    debugger
    switch (action.type){
        case "LOGIN":
            return{
                ...state,
                isAuthenticated: true,
                user: action.payload
            }
    }
}



export default authReducer
