import React from "react";
import {
  Redirect
} from "react-router-dom";

function withAuthRedirect({ children, ...rest }) {
  return (
    <Route
      {...rest}
      render={({ location }) =>
        isAuth ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: "/admin",
              state: { from: location }
            }}
          />
        )
      }
    />
  )
}
export default withAuthRedirect
