import React, {useEffect, useState} from 'react';
import "../Admin.css";


const ShowStudents = () => {
    const [students, setStudents]=useState([]);

    useEffect(() => {
        fetch("http://localhost:3000/students/all")
      .then(response=>{
           return response.json()
      }).then(response=>{
          setStudents(response)
      })
    })

    return (
        <div>
            <table style={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        flexDirection:"row",
                    }}>
                <tbody>
                  <tr>
                    <th>Student ID</th>
                    <th>Name</th>
                    <th>Surname</th>
                    <th>Mark</th>
                    <th>Presence</th>
                    <th>Time</th>
                </tr>
                </tbody>
                
                <tbody>
                    {students.map((val,index) => {
                        return (
                            <tr>
                                <td>{val.student_id}</td>
                                <td>{val.name}</td>
                                <td>{val.surname}</td>
                                <td>{val.presence}</td>
                                <td>{val.time}</td>
                            </tr>)
                    })}
                </tbody>
                
            </table>
        </div>

    )
}
export default ShowStudents
